import React, {useState,useEffect} from 'react';
import {Radar} from 'react-chartjs-2';


const RadarGraph = () =>{

    const [chartData, setChartData] = useState();

    const chart = () =>{
        setChartData({
            labels:['Dev', 'PBD', 'VC', 'Nodejs','CC','dev Theory','MD'],
            datasets: [{
                label: 'Time Spend',
                data: [90, 88, 60, 15, 30, 53, 30],
                fill: true,
                backgroundColor: 'rgba(60, 90, 152, 0.6)',
                borderColor: 'rgb(60, 90, 152)',
                pointBackgroundColor: 'rgb(60, 90, 152)',
                pointBorderColor: '#fff',
                pointHoverBackgroundColor: '#fff',
                pointHoverBorderColor: 'rgb(60, 90, 152)'
              }, {
                label: 'Time Needed',
                data: [100, 30, 55, 60, 20, 97, 20],
                fill: true,
                backgroundColor: 'rgba(150, 100, 235, 0.6)',
                borderColor: 'rgb(150, 100, 235)',
                pointBackgroundColor: 'rgb(150, 100, 235)',
                pointBorderColor: '#fff',
                pointHoverBackgroundColor: '#fff',
                pointHoverBorderColor: 'rgb(54, 100, 235)'
              }]
        });
    }

    useEffect(() =>{
        chart();
    },[]);

        return(
        <div style={{height:'500px', width:'500px',float:'left'}} className='chart_item'>
           <Radar data={chartData} options={{
               responsive:true,
           }}/>

        </div>
    );
}

export default RadarGraph;


const data = {
    labels: [
      'Eating',
      'Drinking',
      'Sleeping',
      'Designing',
      'Coding',
      'Cycling',
      'Running'
    ],
    datasets: [{
      label: 'My First Dataset',
      data: [65, 59, 90, 81, 56, 55, 40],
      fill: true,
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      borderColor: 'rgb(255, 99, 132)',
      pointBackgroundColor: 'rgb(255, 99, 132)',
      pointBorderColor: '#fff',
      pointHoverBackgroundColor: '#fff',
      pointHoverBorderColor: 'rgb(255, 99, 132)'
    }, {
      label: 'My Second Dataset',
      data: [28, 48, 40, 19, 96, 27, 100],
      fill: true,
      backgroundColor: 'rgba(54, 162, 235, 0.2)',
      borderColor: 'rgb(54, 162, 235)',
      pointBackgroundColor: 'rgb(54, 162, 235)',
      pointBorderColor: '#fff',
      pointHoverBackgroundColor: '#fff',
      pointHoverBorderColor: 'rgb(54, 162, 235)'
    }]
  };
