import React, {useState,useEffect} from 'react';
import {Line} from 'react-chartjs-2';


const LineChart = () =>{

    const [chartData, setChartData] = useState();

    const chart = () =>{
        setChartData({
            labels:['Low', 'Mid', 'High', 'Market'],
            datasets:[{
                label:'Holofoil Price',
                data:[14.73,22,99,25.25],
                fill: true,
                backgroundColor:['rgba(75,192,192,0.6)'],
                borderWidth: 4,
                tension: 0.1
            }]
        });
    }

    useEffect(() =>{
        chart();
    },[]);

        return(
            <div className='containerLineChart'><h1 style={{textAlign:'center', marginTop:'-15px'}}>Prices of Cards</h1><br></br>
            <h1 style={{textAlign:'center', marginTop:'-30px',fontSize:'16px'}}>Prices of Cards</h1>
        <div style={{height:'500px', width:'500px', float:'left'}}  className='Prices'>
           <Line data={chartData}  options={{
               responsive:true,
           }}/>
        </div>
        </div>
    );
}

export default LineChart;
