import React, {useState,useEffect} from 'react';
import {Line} from 'react-chartjs-2';
import $ from 'jquery';



const LineChart = (props) =>{

    const [apiValue, setApiValue] = useState([]);
    const [apiValue2, setApiValue2] = useState([]);

    useEffect(() =>{
      
    async function loadData(){
        const response = await fetch("https://pokeapi.co/api/v2/pokemon/" +props.search1);
        const data = await response.json();
        const item = data;
       setApiValue(item);
       console.log(item);

    }

    loadData();

    async function loadData2(){
        const response = await fetch("https://pokeapi.co/api/v2/pokemon/" + props.search2);
        const data = await response.json();
        const item2 = data;
       setApiValue2(item2); 

    }

    loadData2();
    }, [props]);

        const chartData = {
            labels:['HP', 'Attack', 'Defence', 'Speed'],
            datasets:[{
                label:""+props.search1 +"",
                data: [10],
                data:[apiValue.stats[0].base_stat,apiValue.stats[1].base_stat,apiValue.stats[2].base_stat,apiValue.stats[5].base_stat],
                fill: true,
                backgroundColor:['rgba(179, 0, 0,0.2)'],
                borderColor:['rgb(179, 0, 0)'],
                borderWidth: 2, 
                tension: 0.1
            },{
                
                label:""+props.search2+"",
                data: [10],
                // data:[apiValue2.stats[0].base_stat,apiValue2.stats[1].base_stat,apiValue2.stats[2].base_stat,apiValue2.stats[5].base_stat],
                fill: true,
                backgroundColor:['rgba(199, 50, 150,0.2)'],
                borderColor:['rgb(199, 50, 150)'],
                borderWidth: 2, 
                tension: 0.1
            }]
    };
    
    return(
            <div className='containerChart'><h1 style={{textAlign:'center', marginTop:'-15px'}}>Card Prices</h1><br></br>
            <h1 style={{textAlign:'center', marginTop:'-35px',fontSize:'16px', fontWeight:'200'}}>-Compare Pokemon Level-</h1>
        <div style={{height:'500px', width:'500px', float:'left'}}  className='Prices'>
           <Line data={chartData}  options={{
               responsive:true,
           }}/>
        </div>
        </div>
    );
}

export default LineChart;
