import React, {useState,useEffect} from 'react';
import $ from 'jquery';
const PokeCon = () => {
var search = '';
    // function Search(){
    //     search = document.getElementById("search").value;
    //     console.log(search);
    // }

    
$("searchbtn").click(function(){
    search = document.getElementById("search").value;
    console.log(search);
  });

    const [apiValue, setApiValue] = useState([]);
        useEffect(() =>{
            async function loadData(){
                const response = await fetch("https://pokeapi.co/api/v2/pokemon/" + search);
                const data = await response.json();
                const item = data;
               setApiValue(item);  
            }
        
            loadData();
    
            }, []);

           var id = apiValue.id;

            $(".Pokeimg").append("<img src='https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/"+ id +".png' alt='logo' width='100' height='150'></img>");

    return(
        <div>
            <input type='text' className='searchbar' id='search'></input>
            <div className='searchbtn' onClick= "Search()">Search</div>
            <div className='Pokeimg'></div>
        </div>
    );
};

export default PokeCon;
