import React, {useState,useEffect} from 'react';
import {Bar} from 'react-chartjs-2';


const BarChart = () =>{

    const [chartData, setChartData] = useState();

    const chart = () =>{
        setChartData({
            labels:['Apple', 'Pecan', 'Blueberry', 'Chicken','Steak & Kidney','Banana','Pie Flavor'],
            datasets:[{
                label:'Popularity of Pies',
                data:[32,20,45,21,33,15,26],
                backgroundColor:[
                    'rgba(75,192,192,0.6)',
                ],
                borderWidth: 4
            }]
        });
    }

    useEffect(() =>{
        chart();
    },[]);

        return(
        <div className='chart_item'>
           <Bar data={chartData} style={{height:'500px', width:'500px'}}/>

        </div>
    );
}

export default BarChart;
