import React, {useState,useEffect} from 'react';
import $ from 'jquery';
const PokeCon2 = () => {

    var [search2, setSearch] = useState("pikachu");

    const [apiValue, setApiValue] = useState([]);
        useEffect(() =>{
            async function loadData(){
                const response = await fetch("https://pokeapi.co/api/v2/pokemon/" + search2);
                const data = await response.json();
                const item = data;
               setApiValue(item);  
            }
        
            loadData();
    
            }, [search2]);
            var id = apiValue.id;
            $(".Pokeimg").html("<img src='https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/"+ id +".png' alt='logo' width='100' height='150'></img>");


    return(
        <div>

            <div className='Pokeimg2'></div>
        </div>
    );
};

export default PokeCon2;
