import React, {useState,useEffect} from 'react';
import $ from 'jquery';
const PokeCon = () => {

    var [search, setSearch] = useState("ditto");

    const [apiValue, setApiValue] = useState([]);
        useEffect(() =>{
            async function loadData(){
                const response = await fetch("https://pokeapi.co/api/v2/pokemon/" + search);
                const data = await response.json();
                const item = data;
               setApiValue(item);  
            }
        
            loadData();
    
            }, [search]);
            var id = apiValue.id;
            $(".Pokeimg").html("<img src='https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/"+ id +".png' alt='logo' width='100' height='150'></img>");

    function Search(){
      var  s = document.getElementById("search").value;
        console.log(s);
        setSearch(s);
        
       

    }


           
    return(
        <div>
            <input type='text' className='searchbar' id='search'></input>
            <div className='searchbtn' onClick= {Search} >Search</div>
            <div className='Pokeimg'></div>
        </div>
    );
};

export default PokeCon;
