import React, {useState,useEffect} from 'react';
import {Bar} from 'react-chartjs-2';


const BarChart = () =>{

    var stackedBar = new Chart(ctx, {
        type: 'bar',
        data: data,
        options: {
            scales: {
                x: {
                    stacked: true
                },
                y: {
                    stacked: true
                }
            }
        }
    });
    const [chartData, setChartData] = useState();

    const chart = () =>{
        setChartData({
            labels:['Apple', 'Pecan', 'Blueberry', 'Chicken','Steak & Kidney','Banana','Pie Flavor'],
            datasets:[{
                axis:'y',
                label:'Popularity of Pies',
                data:[32,20,45,21,33,15,26],
                backgroundColor:[
                    'rgba(75,192,192,0.6)',
                ],
                borderWidth: 4
            }]
        });
    }

    useEffect(() =>{
        chart();
    },[]);

        return(
            <div className='containerChart'><h1 style={{textAlign:'center', marginTop:'-15px'}}>Prices of Cards</h1><br></br>
            <h1 style={{textAlign:'center', marginTop:'-35px',fontSize:'16px', fontWeight:'200'}}>-Compare HoloFoil and 1st Edition Holofoil-</h1>
        <div style={{height:'500px', width:'500px', float:'left'}}  className='Prices'>
           <Bar data={chartData}  options={{
               responsive:true,
           }}/>
        </div>
        </div>
    );
}

export default BarChart;
