import React, {useState,useEffect} from 'react';
import {Pie} from 'react-chartjs-2';


const PieChart = () =>{

    const [chartData, setChartData] = useState();

    const chart = () =>{
        setChartData({
            labels:['Apple', 'Pecan', 'Blueberry', 'Chicken','Steak & Kidney','Banana','Pie Flavor'],
            datasets:[{
                label:'Popularity of Pies',
                data:[32,20,45,21,33,15,26],
                backgroundColor:[
                    'rgba(75,192,192,0.6)',
                    'rgba(55,54,80,0.6)',
                    'rgba(95,200,200,0.6)',
                    'rgba(69,120,90,0.6)',
                    'rgba(159,50,150,0.6)',
                    'rgba(255,54,160,0.6)',
                    'rgba(200,192,100,0.6)',

                ],
                borderWidth: 4
            }]
        });
    }

    useEffect(() =>{
        chart();
    },[]);

        return(
            <div className='containerChart'><h1 style={{textAlign:'center', marginTop:'-15px'}}>Levels Comparison</h1><br></br>
            <h1 style={{textAlign:'center', marginTop:'-35px',fontSize:'16px', fontWeight:'200'}}>-Compare Pokemon Level-</h1>
        <div style={{height:'300px', width:'300px', float:'left', marginLeft:'70px'}}  className='Prices'>
           <Pie data={chartData}  options={{
               responsive:true,
           }}/>
        </div>
        </div>
    );
}

export default PieChart;
