import React, {useState,useEffect} from 'react';
import {Line} from 'react-chartjs-2';


const LineChart = () =>{

    const [apiValue, setApiValue] = useState([]);
    const [apiValue2, setApiValue2] = useState([]);

    useEffect(() =>{
      
    async function loadData(){
        const response = await fetch("https://pokeapi.co/api/v2/pokemon/ditto");
        const data = await response.json();
        const item = data;
       setApiValue(item);
       console.log(item);

    }

    loadData();

    async function loadData2(){
        const response = await fetch("https://pokeapi.co/api/v2/pokemon/pikachu");
        const data = await response.json();
        const item2 = data;
       setApiValue2(item2); 

    }

    loadData2();
    }, []);


    var hppoke1 = [];
    // var attackpoke1 = [];
    // var defencepoke1 = [];
    // var speedpoke1 = [];
    // var hppoke2 = [];
    // var attackpoke2 = [];
    // var defencepoke2 = [];
    // var speedpoke2 = [];
     hppoke1.push(apiValue.stats[0].base_stat);
    // hppoke2.push(apiValue2.stats[0].base_stat);
    // attackpoke1.push(apiValue.stats[1].base_stat);
    // attackpoke2.push(apiValue2.stats[1].base_stat);
    // defencepoke1.push(apiValue.stats[2].base_stat);
    // defencepoke2.push(apiValue2.stats[2].base_stat);
    // speedpoke1.push(apiValue.stats[5].base_stat);
    // speedpoke2.push(apiValue2.stats[5].base_stat);
     console.log(hppoke1);
    // console.log(hppoke2);
    // console.log(attackpoke1);
    // console.log(attackpoke2);
    // console.log(defencepoke1);
    // console.log(defencepoke2);
    // console.log(speedpoke1);
    // console.log(speedpoke2);


        console.log(apiValue);


        const chartData = {
            labels:['HP', 'Attack', 'Defence', 'Speed'],
            datasets:[{
                label:'Pokemon1',
                data:[apiValue.stats[0].base_stat],
                fill: true,
                backgroundColor:['rgba(179, 0, 0,0.2)'],
                borderColor:['rgb(179, 0, 0)'],
                borderWidth: 2, 
                tension: 0.1
            },{
                
                label:'Pokemon2',
                data:[apiValue2.stats[0].base_stat],
                fill: true,
                backgroundColor:['rgba(199, 50, 150,0.2)'],
                borderColor:['rgb(199, 50, 150)'],
                borderWidth: 2, 
                tension: 0.1
            }]
    };
    
    return(
            <div className='containerChart'><h1 style={{textAlign:'center', marginTop:'-15px'}}>Card Prices</h1><br></br>
            <h1 style={{textAlign:'center', marginTop:'-35px',fontSize:'16px', fontWeight:'200'}}>-Compare Pokemon Level-</h1>
        <div style={{height:'500px', width:'500px', float:'left'}}  className='Prices'>
           <Line data={chartData}  options={{
               responsive:true,
           }}/>
        </div>
        </div>
    );
}

export default LineChart;
