import React, {useState,useEffect} from 'react';
import {Radar} from 'react-chartjs-2';


const RadarChart = () =>{

    const [chartData, setChartData] = useState();

    const chart = () =>{
        setChartData({
            labels:['Dev', 'PBD', 'VC', 'Nodejs','CC','dev Theory','MD'],
            datasets: [{
                label: 'Pokemon 1',
                data: [90, 88, 60, 15, 30, 53, 30],
                fill: true,
                backgroundColor: 'rgba(60, 90, 152, 0.6)',
                borderColor: 'rgb(60, 90, 152)',
                pointBackgroundColor: 'rgb(60, 90, 152)',
                pointBorderColor: '#fff',
                pointHoverBackgroundColor: '#fff',
                pointHoverBorderColor: 'rgb(60, 90, 152)'
              }, {
                label: 'Pokemon 2',
                data: [100, 30, 55, 60, 20, 97, 20],
                fill: true,
                backgroundColor: 'rgba(150, 100, 235, 0.6)',
                borderColor: 'rgb(150, 100, 235)',
                pointBackgroundColor: 'rgb(150, 100, 235)',
                pointBorderColor: '#fff',
                pointHoverBackgroundColor: '#fff',
                pointHoverBorderColor: 'rgb(54, 100, 235)'
              }]
        });
    }

    useEffect(() =>{
        chart();
    },[]);

        return(
        <div style={{height:'500px', width:'500px',float:'left'}} className='chart_item'>
           <Radar data={chartData} options={{
               responsive:true,
           }}/>

        </div>
    );
}

export default RadarChart;
