import React, {useState, useEffect } from "react";
import { Line } from "react-chartjs-2";

const LineChart = () => {

    const [apiValue, setApiValue] = useState([]);

    useEffect(() =>{
    async function loadData(){
        const response = await fetch("https://api.pokemontcg.io/v2/cards?q=name:ditto");
        const data = await response.json();
        const item = data.data;
       setApiValue(item);  
    }

    loadData();
    }, []);

    console.log(apiValue);

    const pricelow = apiValue.filter((v) => v.prices.holofoil.low);
    const pricemid = apiValue.filter((v) => v.prices.holofoil.mid);
    const pricehigh = apiValue.filter((v) => v.prices.holofoil.high);
    const pricemarket = apiValue.filter((v) => v.prices.holofoil.market);



    const chartData = {
        labels: ["Low","mid","high","market"],
        datasets: [{
            label: " Holofoil Card Prices", 
            data: [pricelow,pricemid,pricehigh,pricemarket],
            backgroundColor: ['rgba(75,192,192,0.6)','rgba(255,192,192,0.6)' ],
            borderWidth: 4,
        }]
    };

    return(
                    <div className='containerChart'><h1 style={{textAlign:'center', marginTop:'-15px'}}>Levels Comparison</h1><br></br>
                    <h1 style={{textAlign:'center', marginTop:'-35px',fontSize:'16px', fontWeight:'200'}}>-Compare Pokemon Level-</h1>
                <div style={{height:'500px', width:'500px', float:'left'}}  className='Prices'>
                   <Line data={chartData}  options={{
                       responsive:true,
                   }}/>
                </div>
                </div>
            );

}

export default LineChart;
