import React, {useState,useEffect} from 'react';
import {Bar} from 'react-chartjs-2';


const BarChart = () =>{

    
    const [chartData, setChartData] = useState();

    const chart = () =>{
        setChartData({
            labels:['Pokemon 1', 'Pokemon 2'],
            datasets:[{
                label:'Level',
                data:[50,20],
                backgroundColor:[
                    'rgba(179, 0, 0,0.6)',
                    'rgba(179, 0, 0,0.6)',
                ],
                borderWidth: 2
            }]
        });
        
    }

    useEffect(() =>{
        chart();
    },[]);

        return(
            <div className='containerChart'><h1 style={{textAlign:'center', marginTop:'-15px'}}>Prices of Cards</h1><br></br>
            <h1 style={{textAlign:'center', marginTop:'-35px',fontSize:'16px', fontWeight:'200'}}>-Compare HoloFoil and 1st Edition Holofoil-</h1>
        <div style={{height:'500px', width:'500px', float:'left'}}  className='Prices'>
           <Bar data={chartData}  options={{
               responsive:true,
           }}/>
        </div>
        </div>
    );
}

export default BarChart;
